#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1) provided by ChatGPT
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / (iResolution.y);

    vec3 cameraOrigin = vec3(0.0, 0.0, 4.08);

    float radfov = (60.0 / 2.0) * PI / 180.0;
    float focalLength = 1.0 / tan(radfov);

    vec3 direction = normalize(vec3(uv, -0.5*focalLength));
    vec3 normal = vec3(-0.8524684670021339, 0.52277864605111, 0.0);

    vec3 planeColor = vec3(0.0);

    vec3 center = vec3(0.0);
    float inR = 0.3;
    float outR = 0.7;

    vec3 inColor = vec3(0.19, 0.3, 0.2);
    vec3 outColor = vec3(0.94, 0.97, 0.68);
    vec3 distColor = outColor - inColor;

    float t = (0.0-cameraOrigin.z) / direction.z;

    vec3 p = cameraOrigin + t * direction;

    if (length(p.xy) > inR && length(p.xy) < outR){

        // Asked ChatGPT how to linearrly mix the colors
        // Mix linearly blends colors between inner and outer radius.
        float alpha = (length(p.xy) - inR) / (outR - inR); // normalized 0â†’1
