uvec4 pcg4d(inout uvec4 seed)
{
    seed = seed * 1664525u + 1013904223u;
    seed += seed.yzxy * seed.wxyz;
    seed = (seed >> 16) ^ seed;
    seed += seed.yzxy * seed.wxyz;
    return seed;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord/iResolution.xy;

    uvec4 seed = uvec4(fragCoord.x, fragCoord.y, iFrame, 146);

    // Use the seed to generate random vec4
    // And normalized pixel coordinates (from 0 to 1)
    vec4 random_between_zero_and_one = vec4(pcg4d(seed)) / vec4(0xFFFFFFFFu);

    // Set fragColor to the random noise
    fragColor = random_between_zero_and_one;
}
