#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1) provided by ChatGPT
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / (iResolution.y);

    vec3 cameraOrigin = vec3(0.0, 0.0, 4.08);
    vec3 center = vec3(0.0,0.0,0.0);
    float radius = 1.1;
    vec3 oc = cameraOrigin - center;

    float radfov = (60.0 / 2.0) * PI / 180.0;
    float focalLength = 1.0 / tan(radfov);

    vec3 direction = normalize(vec3(uv, -0.5*focalLength));

    vec3 normal = vec3(-0.8524684670021339, 0.52277864605111, 0.0);

    vec3 planeColor = vec3(0.0, 0.0, 0.0);

    float a = dot(direction,direction);
    float b = 2.0 * dot(direction,oc);
    float c = dot(oc, oc) - radius * radius;

    float discriminant = b*b - 4.0*a*c;

    if (discriminant >= 0.0) {

        //Lower value
        float t = (-b - sqrt(discriminant)) / (2.0 * a);

        // Equation p=o+t*d
        vec3 p = cameraOrigin + t * direction;

        //Normalize aka divide by vectors lenght
        vec3 n = normalize(p - center);

        // We are using coordinates [-1,1] so going back to [0,1]
        planeColor = n * 0.5 + 0.5;



    }
    // Output to screen
    fragColor = vec4(planeColor,1.0);
}
