// Found this equation from Sebastian Lague
// https://www.youtube.com/watch?v=HYAgJN3x4GA
bool pointInTriangle (vec2 pt, vec2 v1, vec2 v2, vec2 v3)
{
    float w1 = (v1.x * (v3.y - v1.y) + (pt.y - v1.y) * (v3.x - v1.x) - pt.x * (v3.y - v1.y)) / ((v2.y - v1.y) * (v3.x - v1.x) - (v2.x - v1.x) * (v3.y - v1.y));
    float w2 = (pt.y - v1.y - w1 * (v2.y-v1.y)) / (v3.y - v1.y);

    return (w1 + w2) <= 1.0 && w1 >= 0.0 && w2 > 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 side1 = vec2(-0.88, -0.65);
    vec2 side2 = vec2(0.65, -0.8);
    vec2 side3 = vec2(-0.05, 0.83);


    vec3 bgColor = vec3(0,0,0);
    vec3 fillColor = vec3(0.67, 0.37, 0.63);

    // Normalized device coordinates AKA NDC
    // First we normalize pixel and then change it to NDC
    vec2 p = (fragCoord / iResolution.xy) * 2.0 - 1.0;

    // if in triangle fill with purple
    if (pointInTriangle(p, side1, side2, side3)){
        bgColor = fillColor;
    }

    // Output to screen
    fragColor = vec4(bgColor,1.0);
}
