#define PI 3.14159265359


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{

    float aspect = iResolution.x / iResolution.y;

    float fovAngle = (64.0 / 2.0) * PI / 180.0;

    float Z = tan(fovAngle);

    // NDC values are in range [0,1]
    // They can be shigted by multiplying by 2 and subrtacting 1
    float ndcX = fragCoord.x / iResolution.x;
    float ndcY = fragCoord.y / iResolution.y;

    // To make pixels square again we need to multiply x-direction
    // with aspect ration because there are more pixels horizontally
    // Also multiply with Z because accounting for the FOV
    float X = (2.0 * ndcX - 1.0) * aspect * Z;
    float Y = (2.0 * ndcY - 1.0) * Z;

    vec3 raydir = normalize(vec3(X,Y,-1.0));

    // Output to screen
    fragColor = vec4(raydir * 0.5 + 0.5, 1.0);
}
