#define PI 3.14159265359

vec3 toSRGB(vec3 c)   { return pow(c, vec3(1.0 / 2.2)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / (iResolution.y);

    vec3 cameraOrigin = vec3(0,0.62,0.88);
    vec3 center = vec3(0.0,0.62,-1.92);
    float radius = 0.62;
    vec3 oc = cameraOrigin - center;

    float radfov = (60.0 / 2.0) * PI / 180.0;
    float focalLength = 1.0 / tan(radfov);

    vec3 direction = normalize(vec3(uv, -0.5*focalLength));

    vec3 planeColor = vec3(0.0, 0.0, 0.0);
    vec3 sunDirection = vec3(0.6878046006560913, 0.690049317018142, 0.22529263503077648);
    vec3 sunColor =  vec3(3.0, 3.0, 3.0);
    vec3 sphereDColor =  vec3(0.87, 0.77, 0.75);
    vec3 planeDColor = vec3(0.67, 0.3, 0.42);
    float sphereRoughness = 0.27;
    float planeRoughness = 0.22;

    float a = dot(direction,direction);
    float b = 2.0 * dot(direction,oc);
    float c = dot(oc, oc) - radius * radius;

    float discriminant = b*b - 4.0*a*c;

    vec3 normal = vec3(0.0, 1.0, 0.0);

    if (discriminant >= 0.0) {

        //Lower value
        float t = (-b - sqrt(discriminant)) / (2.0 * a);

        // Equation p=o+t*d
        vec3 p = cameraOrigin + t * direction;

        //Normalize aka divide by vectors lenght
        vec3 n = normalize(p - center);

        vec3 L = normalize(sunDirection);

        float NdotL = max(dot(n, L), 0.0);

        vec3 shadeColor = (sphereDColor / PI)* sunColor * NdotL;

        planeColor = shadeColor;

    }

    else if (abs(dot(normal, direction)) > 1e-06) {
       float t = (-dot(normal, cameraOrigin)) / dot(normal, direction);

       if (t > 0.0){
           // Equation p=o+t*d
        vec3 p = cameraOrigin + t * direction;

        vec3 L = normalize(sunDirection);

        float NdotL = max(dot(normal, L), 0.0);

        vec3 shadeColor = (planeDColor / PI) * sunColor * NdotL;

        planeColor = shadeColor;
       }
    }


    planeColor = toSRGB(planeColor);
    // Output to screen
    fragColor = vec4(planeColor,1.0);
}
