#define PI 3.14159265359

vec3 toSRGB(vec3 c)   { return pow(c, vec3(1.0 / 2.2)); }

bool intersect_scene(
    vec3 ray_origin,
    vec3 ray_direction,
    out vec3 hit_normal,
    out int hit_object_index,
    out float hit_t
){

    hit_t = 1e9;
    hit_object_index = -1;

    {
        vec3 center = vec3(0.0,0.62,-1.92);
        float radius = 0.62;
        vec3 oc = ray_origin - center;


        float a = dot(ray_direction,ray_direction);
        float b = 2.0 * dot(ray_direction,oc);
        float c = dot(oc, oc) - radius * radius;

        float discriminant = b*b - 4.0*a*c;

        if (discriminant >= 0.0) {

            //Lower value
            float t = (-b - sqrt(discriminant)) / (2.0 * a);

            vec3 p = ray_origin + t * ray_direction;

            //Normalize aka divide by vectors lenght
            vec3 n = normalize(p - center);

            if (t > 0.0 && t < hit_t){
                hit_t = t;
                hit_normal = n;
                hit_object_index = 0;
            }
        }
    }

    {
        vec3 normal = vec3(0.0, 1.0, 0.0);

        if (abs(dot(normal, ray_direction)) > 1e-06) {
           float t = (-dot(normal, ray_origin)) / dot(normal, ray_direction);

           if (t > 0.0 && t < hit_t){
               hit_t = t;
               hit_normal = normal;
               hit_object_index = 1;
           }
       }
    }

    return hit_object_index != -1;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / (iResolution.y);

    vec3 cameraOrigin = vec3(0.0,0.62,0.88);

    float radfov = (60.0 / 2.0) * PI / 180.0;
    float focalLength = 1.0 / tan(radfov);

    vec3 direction = normalize(vec3(uv, -0.5*focalLength));

    vec3 planeColor = vec3(0.0, 0.0, 0.0);
    vec3 sunDirection =  	vec3(0.6878046006560913, 0.690049317018142, 0.22529263503077648);
    vec3 sunColor = vec3(3.0, 3.0, 3.0);
    vec3 sphereDColor = vec3(0.87, 0.77, 0.75);
    vec3 planeDColor =  vec3(0.67, 0.3, 0.42);
    float sphereRoughness = 0.27;
    float planeRoughness = 0.22;
    vec3 f0 = vec3(0.04);

    vec3 normal;
    int obj;
    float t;

    if (intersect_scene(cameraOrigin, direction, normal, obj, t)) {
        vec3 p = cameraOrigin + t * direction;

        vec3 L = normalize(sunDirection);
        float NdotL = max(dot(normal, L), 0.0);
        vec3 V = normalize(cameraOrigin - p);

        vec3 halfVector = normalize(L+V);

        float alpha;

        vec3 color;

        if (obj == 0) {color = sphereDColor / PI; alpha = sphereRoughness;}
        if (obj == 1) {color = planeDColor / PI; alpha = planeRoughness;}

        float my_i = max(0.0,dot(normal,L));
        float my_o = max(0.0,dot(normal,V));

        float leftSide = my_o * sqrt(pow(alpha,2.0)+my_i*(my_i-pow(alpha,2.0)*my_i));
        float rightSide = my_i * sqrt(pow(alpha,2.0)+my_o*(my_o-pow(alpha,2.0)*my_o));

        float G = 0.5 / (leftSide + rightSide);
        float D = pow(alpha, 2.0) / (PI * pow((1.0 + pow(dot(normal, halfVector),2.0)*(pow(alpha, 2.0)-1.0)),2.0));
        vec3 F = f0 + (1.0-f0)*pow((1.0-max(0.0,dot(halfVector,L))),5.0);

        vec3 G_vec3 = vec3(G);
        vec3 D_vec3 = vec3(D);

        planeColor = F * G_vec3 * D_vec3 * sunColor * NdotL;


    }

    planeColor = toSRGB(planeColor);
    // Output to screen
    fragColor = vec4(planeColor,1.0);
}
