#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from -1 to 1) provided by ChatGPT
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 cameraOrigin = vec3(0.0, 0.0, 4.08);

    float radfov = 60.0 * PI / 180.0;
    float focalLength = 1.0 / tan(radfov / 2.0);

    vec3 direction = normalize(vec3(uv, 0.5*focalLength));

    vec3 normal = vec3(-0.8524684670021339, 0.52277864605111, 0.0);

    float D = -2.0;

    vec3 planeColor = vec3(1.0, 1.0, 1.0);

    // N * P = D and P = origin + t*direction
    // N * (origin + t*direction) = D
    // N * origin + N * t*direction = D
    // t = (D - N * origin) / N * direction

    if (abs(dot(normal, direction)) > 1e-06) {
       float t = (D-dot(normal, cameraOrigin)) / dot(normal, direction);

       if (t > 0.0){
           planeColor = vec3(0.92, 0.86, 0.85) / t;
       }
    }

    // Output to screen
    fragColor = vec4(planeColor,1.0);
}
